export default function userReducer(state, action) {
  switch (action.type) {
    case "updateName":
      state[0].name = "Bekir";

      fetch("http://dummy.restapiexample.com/api/v1/employees")
        .then((res) => res.json())
        .then((res) => {
          if (res.error) {
            throw res.error;
          }
          console.log(res);
          return [...state, res.data];
        })
        .catch((error) => {
          console.log(error);
        });

      return [...state];
    default:
      return state;
  }
}
