export default function userReducer(state, action) {
  switch (action.type) {
    case "updateName":
      console.log(state);

      let data = fetch("https://exampleapi.com/products")
        .then((res) => res.json())
        .then((res) => {
          if (res.error) {
            throw res.error;
          }

          return res.products;
        })
        .catch((error) => {});

      return [data];
    default:
      return state;
  }
}
