export default async function userReducer(state, action) {
  switch (action.type) {
    case "updateName":
      state[0].employee_name = "Bekir";

      let res = await fetch("http://dummy.restapiexample.com/api/v1/employees");
      res = await res.json();
      console.log(res);

      return res.formData;
    default:
      return state;
  }
}
