  switch (action.type) {
    case "updateName":
      // let res = await fetch("http://dummy.restapiexample.com/api/v1/employees");
      // res = await res.json();
      // console.log(res);
      state.push({
        id: "1",
        employee_name: "ahmet Nixon",
        employee_salary: "320800",
        employee_age: "22",
        profile_image: "",
      });

      return [...state];
    default:
      return state;
  }
}
