import { createBottomTabNavigator } from "@react-navigation/bottom-tabs";
import * as React from "react";

import TabBarIcon from "../components/TabBarIcon";
import CreditScreen from "../screens/CreditScreen";
import MTVScreen from "../screens/MTVScreen";
import FilterScreen from "../screens/FilterScreen";
import CompanyScreen from "../screens/CompanyScreen";
import AboutScreen from "../screens/AboutScreen";

const BottomTab = createBottomTabNavigator();
const INITIAL_ROUTE_NAME = "Home";

export default function BottomTabNavigator({ navigation, route }) {
  navigation.setOptions({ headerTitle: getHeaderTitle(route) });

  return (
    <BottomTab.Navigator initialRouteName={INITIAL_ROUTE_NAME}>
      <BottomTab.Screen
        name="Credit"
        component={CreditScreen}
        options={{
          title: "Kredi",
          tabBarIcon: ({ focused }) => <TabBarIcon focused={focused} name="star" />,
        }}
      />
      <BottomTab.Screen
        name="MTV"
        component={MTVScreen}
        options={{
          title: "Motorlu Taşıtlar Vergisi",
          tabBarIcon: ({ focused }) => <TabBarIcon focused={focused} name="layer-group" />,
        }}
      />
      <BottomTab.Screen
        name="Filter"
        component={FilterScreen}
        options={{
          title: "Filtreler",
          tabBarIcon: ({ focused }) => <TabBarIcon focused={focused} name="bell" />,
        }}
      />
      <BottomTab.Screen
        name="Company"
        component={CompanyScreen}
        options={{
          title: "Şirketler",
          tabBarIcon: ({ focused }) => <TabBarIcon focused={focused} name="award" />,
        }}
      />
      <BottomTab.Screen
        name="About"
        component={AboutScreen}
        options={{
          title: "İletişim",
          tabBarIcon: ({ focused }) => <TabBarIcon focused={focused} name="envelope" />,
        }}
      />
    </BottomTab.Navigator>
  );
}

function getHeaderTitle(route) {
  const routeName = route.state?.routes[route.state.index]?.name ?? INITIAL_ROUTE_NAME;

  switch (routeName) {
    case "Home":
      return "Home";
    case "News":
      return "News";
    case "Filter":
      return "Filter";
    case "Company":
      return "Company";
    case "About":
      return "About";
  }
}
